import{a as d}from"./chunk-3YIIC44M.js";import"./chunk-UWSC7YQZ.js";import{a as l}from"./chunk-4PMUWLZA.js";import"./chunk-NSB2CLGN.js";import{c as o}from"./chunk-CWKAQHZI.js";import"./chunk-W62X2L65.js";import"./chunk-7V6WA5A4.js";import"./chunk-YHHGV7YW.js";import"./chunk-AQVF375O.js";import"./chunk-SRQXZXE3.js";import{Pb as s,Sb as r,mb as a,tb as n}from"./chunk-3VK5VDXH.js";var b=class i{constructor(t){this.breadcrumbService=t;this.breadcrumbService.setBreadcrumbs([{label:"Home",url:"/home"},{label:"Paradigms",url:"/paradigms"},{label:"P300",url:"/paradigms/p300"}])}p300={section:"P300",link:"/paradigms/p300",context:"The <b>P300 paradigm</b> enables the detection of attention-related brain responses by presenting <b>infrequent target stimuli</b> within a stream of frequent ones. It is widely used in <b>cognitive neuroscience</b> and <b>brain-computer interfaces (BCIs)</b> to decode user intent and measure cognitive processing.",definition:"P300 is a <b>positive deflection</b> in the EEG signal that occurs approximately <b>300 ms</b> after the presentation of an <b>infrequent, task-relevant stimulus</b> among a sequence of frequent events. It is one of the most studied <b>event-related potentials (ERPs)</b>.",application:"P300 paradigms are applied in <b>communication systems</b> (e.g., spellers), <b>diagnostic tools</b>, <b>attention tracking</b>, and <b>hybrid BCI frameworks</b> that combine multiple neural signals for enhanced control.",description:"The <b>P300 component</b> reflects <b>cognitive processes</b> related to <b>stimulus evaluation</b>, <b>context updating</b>, and <b>decision-making</b>. It is typically elicited through <b>oddball paradigms</b> using visual, auditory, or tactile modalities. These experiments are ideal for studying <b>time-locked brain responses</b> and training machine learning models to detect <b>attention-driven brain activity</b>.",explore:"Explore P300 Experiments",explore_text:"The following experiments showcase distinct implementations of the <b>P300 paradigm</b>, each differing in <b>stimulus modality</b>, <b>timing</b>, and <b>task demands</b>. They include simple oddball structures, speller systems, and rapid presentation streams, enabling the study of <b>target detection</b>, <b>attentional modulation</b>, and <b>ERP classification</b> in diverse BCI settings.",mechanism:"P300 responses are generated when a subject detects a <b>rare or meaningful event</b>, typically involving the <b>posterior parietal cortex</b> and <b>temporal-parietal junction</b>. This response is linked to <b>stimulus evaluation</b>, <b>attention allocation</b>, and <b>context updating</b>, and is easily observable in scalp EEG recordings at <b>central-parietal electrodes</b> (e.g., Cz, Pz).",experiments:[{name:"Visual Oddball",link:"/paradigms/p300/visual-oddball",tier:1,description:"This basic paradigm uses two visual stimuli with differing probabilities. Participants attend to the rare 'target' stimulus, which elicits a clear P300 response. It is ideal for <b>calibration and classification training</b>."},{name:"Three-Stimulus Oddball",link:"/paradigms/p300/three-stimulus",tier:2,description:"Expands the classical oddball by adding <b>non-target novel stimuli</b>, allowing the separation of <b>P3a (novelty)</b> and <b>P3b (task relevance)</b> components. Useful for studying <b>attention switching</b> and <b>cognitive control</b>."},{name:"P300 Speller",link:"/paradigms/p300/speller",tier:2,description:"Implements the <b>Farwell & Donchin</b> speller using a matrix of flashing characters. The system identifies intended letters based on row and column flashes that evoke P300 responses. Commonly used for <b>non-verbal communication</b>."},{name:"RSVP Speller",link:"/paradigms/p300/rsvp",tier:2,description:"Presents stimuli one-by-one at high speed in the center of the screen. Participants focus on their target as it appears in the stream, producing a P300 response. This design reduces eye movement requirements and supports <b>sequential selection</b>."},{name:"Auditory Oddball",link:"/paradigms/p300/auditory-oddball",tier:2,description:"Uses sequences of auditory tones with occasional target sounds. The user attends to the target tone, eliciting a P300 response. Suitable for <b>vision-impaired users</b> or testing <b>cross-modal attention</b>."}]};static \u0275fac=function(e){return new(e||i)(a(l))};static \u0275cmp=n({type:i,selectors:[["app-p300"]],decls:1,vars:1,consts:[[3,"paradigm"]],template:function(e,c){e&1&&r(0,"app-paradigm",0),e&2&&s("paradigm",c.p300)},dependencies:[o,d],encapsulation:2})};export{b as P300};
