import{a as b}from"./chunk-TULW32F7.js";import"./chunk-2J46Y3L2.js";import{a as c,b as l}from"./chunk-6ZPJXFSI.js";import"./chunk-STTCBUMV.js";import{c as o}from"./chunk-63RWRTVN.js";import"./chunk-6JWNTJQL.js";import"./chunk-IZCHANQD.js";import"./chunk-K7KCRJWB.js";import"./chunk-7AW62Q5F.js";import"./chunk-5REQRI7Z.js";import{Pb as s,Sb as a,mb as t,tb as n}from"./chunk-TR6HYK2P.js";var p=class r{constructor(i,e){this.navigationService=i;this.breadcrumbService=e;i.setActiveLink("/paradigms/ssvep"),this.breadcrumbService.setBreadcrumbs([{label:"Home",url:"/home"},{label:"Paradigms",url:"/paradigms"},{label:"SSVEP",url:"/paradigms/ssvep"}])}escapeHtml(i){let e=document.createElement("p");return e.innerText=i,e.innerHTML}ssvep={section:"SSVEP",link:"/paradigms/ssvep",context:"The <b>SSVEP paradigm</b> enables the detection of <b>visual attention</b> by presenting <b>flickering stimuli</b> at specific frequencies. It is extensively used in <b>brain-computer interfaces (BCIs)</b> for its <b>high signal-to-noise ratio</b>, <b>low training requirement</b>, and <b>real-time responsiveness</b>.",definition:"SSVEP (Steady-State Visually Evoked Potential) is an <b>oscillatory EEG response</b> elicited when a person fixates on a visual stimulus flickering at a <b>constant frequency</b>. The brain activity at the <b>same frequency and its harmonics</b> can be detected over the <b>occipital cortex</b>.",application:"SSVEP paradigms are used in <b>speller systems</b>, <b>control interfaces</b> (e.g., wheelchairs, drones), <b>neurofeedback platforms</b>, and <b>hybrid BCI systems</b> that combine multiple neural features for robust interaction.",description:"The <b>SSVEP response</b> reflects the brain's <b>phase-locked activity</b> to periodic visual stimuli, making it ideal for <b>frequency tagging</b> and <b>multi-class classification</b>. SSVEP-based experiments are well-suited for evaluating <b>visual attention</b> and for implementing <b>high-speed BCIs</b> with minimal calibration.",explore:"Explore SSVEP Experiments",explore_text:"The following experiments showcase different implementations of the <b>SSVEP paradigm</b>, varying in <b>stimulus layout</b>, <b>frequency ranges</b>, and <b>interaction modes</b>. They support applications in <b>frequency-based control</b>, <b>attention decoding</b>, and <b>real-time classification</b> for diverse BCI scenarios.",mechanism:"SSVEPs are generated in the <b>visual cortex</b>, particularly the <b>occipital lobe</b>, when the user fixates on a <b>repetitive visual stimulus</b>. The EEG exhibits increased power at the <b>stimulus frequency</b>, detectable using <b>spectral analysis</b> (e.g., FFT, CCA). This response is <b>strong, sustained</b>, and well-suited for decoding intent with few electrodes (e.g., Oz, O1, O2).",experiments:[{name:"SSVEP Direction Selector",link:"/paradigms/ssvep/direction",tier:1,description:"Displays <b>arrow icons flickering at different frequencies</b> (e.g., \u2191 \u2193 \u2190 \u2192). Users control navigation by looking at the arrow that corresponds to the intended direction. Common in <b>wheelchair and drone interfaces</b>."},{name:"SSVEP Frequency Tuning",link:"/paradigms/ssvep/tuning",tier:2,description:"Explores user-specific responses to a range of <b>flicker frequencies</b> (e.g., 8\u201330 Hz). Participants fixate on each stimulus in turn, helping determine the <b>optimal frequency range</b> for comfort and EEG signal quality. Useful for <b>personalizing SSVEP interfaces</b>."},{name:"SSVEP Speller Grid",link:"/paradigms/ssvep/speller",tier:2,description:"Implements a <b>grid of flashing characters</b>, each tagged with a unique frequency. Users fixate on their desired letter, and the system decodes intent based on <b>frequency-specific EEG responses</b>. Ideal for <b>non-verbal communication</b>."}]};static \u0275fac=function(e){return new(e||r)(t(c),t(l))};static \u0275cmp=n({type:r,selectors:[["app-ssvep"]],decls:1,vars:1,consts:[[3,"paradigm"]],template:function(e,m){e&1&&a(0,"app-paradigm",0),e&2&&s("paradigm",m.ssvep)},dependencies:[o,b],encapsulation:2})};export{p as SSVEP};
